```csharp
using UnityEngine;

public class Clock : MonoBehaviour
{
    [SerializeField]
    private Transform hourHand;
    [SerializeField]
    private Transform minuteHand;
    [SerializeField]
    private Transform secondHand;
    [SerializeField]
    private bool realTime;

    private void Update()
    {
        if (realTime)
        {
            UpdateHandsRealTime();
        }
        else
        {
            UpdateHandsBasedOnInGameTime(Time.time);
        }
    }

    private void UpdateHandsRealTime()
    {
        System.DateTime currentTime = System.DateTime.Now;
        float hours = currentTime.Hour % 12;
        float minutes = currentTime.Minute;
        float seconds = currentTime.Second;

        UpdateHands(hours, minutes, seconds);
    }

    private void UpdateHandsBasedOnInGameTime(float time)
    {
        float hours = Mathf.Floor(time / 3600);
        float minutes = Mathf.Floor((time % 3600) / 60);
        float seconds = Mathf.Floor(time % 60);

        UpdateHands(hours, minutes, seconds);
    }

    private void UpdateHands(float hours, float minutes, float seconds)
    {
        float secondDegrees = 360f * (seconds / 60);
        float minuteDegrees = 360f * (minutes / 60) + 6f * (seconds / 60); // 6 degrees per second
        float hourDegrees = 360f * (hours / 12) + 30f * (minutes / 60); // 30 degrees per minute

        secondHand.localEulerAngles = new Vector3(0, 0, -secondDegrees);
        minuteHand.localEulerAngles = new Vector3(0, 0, -minuteDegrees);
        hourHand.localEulerAngles = new Vector3(0, 0, -hourDegrees);
    }
}
```

