            // Mover la gema a la posición del objetivo delay 1 segundo
            gem.transform.DOMove(targetPosition.transform.position, 3f).SetDelay(2f)
                .OnStart(() => particleSystem1.Play()).OnComplete(() => Destroy(gem));
        }

        // Hacer que las gemas caigan hacia abajo
        var listaPrimerBlanco = new List<(int, int)>();
        var listaGemas = new List<(int, int)>();
        for (int col = 0; col < N; col++)
        {
            // extraer las gemas no vacías en la columna actual
            var nonEmptyGems = new List<int>();
            for (int row = 0; row < board.GetLength(0); row++)
            {
                if (board[row, col] != -1)
                {
                    nonEmptyGems.Add(board[row, col]);
                }
            }

            var primerFlag = false;
            for (int row = board.GetLength(0) - 1; row >= 0; row--)
            {
                if (board[row, col] != -1)
                {
                    if (primerFlag)
                    {
                        listaGemas.Add((row, col));
                    }
                }
                else
                {
                    primerFlag = true;
                }
            }

            if (nonEmptyGems.Count == 6) continue;
            // agregar valores nulos al principio de la lista nonEmptyGems para que tenga N elementos
            nonEmptyGems.InsertRange(0, Enumerable.Repeat(-1, board.GetLength(0) - nonEmptyGems.Count));

            // reemplazar las gemas en la columna actual con la lista nonEmptyGems actualizada
            // Assign the values from the nonEmptyGems list to the board array, column by column.
            var flag = false;
            for (int row = board.GetLength(0) - 1; row >= 0; row--)
            {
                if (!flag && board[row, col] == -1)
                {
                    flag = true;
                    listaPrimerBlanco.Add((row, col));
                }

                board[row, col] = nonEmptyGems[row];
            }
        }
"

To integrate TailwindCSS into an NX workspace with a StencilJS and Angular package, follow these steps:

1. Install TailwindCSS and its dependencies:

   First, navigate to your NX workspace root folder using your terminal.

   Run the following command:

   ```
   npm install tailwindcss postcss autoprefixer -D
   ```

   This command installs TailwindCSS, PostCSS, and Autoprefixer.

2. Create a configuration file:

   At the root of your NX workspace, create a new file named `tailwind.config.js`.

   Add the following content to this file:

   ```javascript
   module.exports = {
     purge: ['./apps/**/*.{html,ts}', './libs/**/*.{html,ts}'], // By default, purge only the Angular and Stencil project files
     theme: {
       extend: {},
     },
     variants: {
       extend: {},
     },
     plugins: [],
   };
   ```

3. Configure TailwindCSS for StencilJS:

   A. Open the `stencil.config.ts` file in your StencilJS package folder.

   B. Import `postcss`:

   ```typescript
   import { Config } from '@stencil/core';
   import { postcss } from '@stencil/postcss';
   import autoprefixer from 'autoprefixer';
   import tailwindcss from 'tailwindcss';
   ```

   C. Add the following to the `plugins` array in the `config` object:

   ```typescript
   postcss({
     plugins: [autoprefixer(), tailwindcss('./tailwind.config.js')],
   }),
   ```

   This will configure StencilJS to use PostCSS with TailwindCSS and Autoprefixer.

4. Configure TailwindCSS for Angular:

   A. In your Angular package folder, create a file named `styles/tailwind.scss`.

   B. Add the following content to `tailwind.scss`:

   ```scss
   @import 'tailwindcss/base';
   @import 'tailwindcss/components';
   @import 'tailwindcss/utilities';
   ```

   C. Open the `angular.json` file in your Angular package folder.

   D. In the `fileReplacements` section, replace the default `styles.css` file with the newly created `styles/tailwind.scss` file. Update the `styles` array to include the `styles/tailwind.scss` file:

   ```json
   "styles": ["apps/angular-app/src/styles/tailwind.scss"],
   ```

   E. Now, open the `postcss.config.js` file in your Angular package folder.

   F. Add the following configuration to the file:

   ```javascript
   const tailwindcss = require('tailwindcss');

   module.exports = {
     plugins: [
       require('postcss-import'),
       tailwindcss('./tailwind.config.js'),
       require('autoprefixer'),
     ],
   };
   ```

5. Import and use TailwindCSS styles in your components:

   A. In your StencilJS and Angular components, add the following line to import the TailwindCSS styles:

   ```html
   <link href="https://cdn.jsdelivr.net/npm/tailwindcss@2.2.16/dist/tailwind.min.css" rel="stylesheet">
   ```

   B. Use the TailwindCSS utility classes in your HTML templates.

Now, you have successfully integrated TailwindCSS into your NX workspace with a StencilJS and an Angular package.

Since Nuxt 3 is still in beta, the following example is based on Nuxt 2 alongside the Nuxt Image module, and it demonstrates creating a custom provider for Imgproxy.net.

First, install the required dependencies in your Nuxt.js project:

```bash
npm install @nuxt/image
```

Next, create a new plugin file called `imgproxy-provider.js` inside the `plugins` directory:

```javascript
// plugins/imgproxy-provider.js

import { createOperationsGenerator } from '@nuxt/image/dist/runtime/providers/utils'
import { joinURL, encodePath } from 'ufo'

const operationsGenerator = createOperationsGenerator({
  key: 'format',
  valueMap: {
    'avif': 'f:avif',
    'jpeg': 'f:jpeg',
    'jpg': 'f:jpg',
    'png': 'f:png',
    'svg': 'f:svg',
    'webp': 'f:webp',
  }
})

export default function (provider, options) {
  provider.provider = {
    getImage: (src, { modifiers = {}, baseURL = options.baseURL } = {}) => {
      const operations = operationsGenerator(modifiers)
      const format = (operations.match(/f:(\w+)/) || [])[1]

      const path = [options.salt, src, operations].join('/')
      const encodedPath = encodePath(path)

      return {
        url: joinURL(baseURL, encodedPath),
        format,
      }
    },
  }
}
```

Now, update your `nuxt.config.js` file to include the @nuxt/image module and register the new custom provider:

```javascript
// nuxt.config.js

export default {
  plugins: ['~/plugins/imgproxy-provider.js'],
  buildModules: [
    ...
    '@nuxt/image',
  ],
  image: {
    providers: {
      imgproxy: {
        name: 'imgproxy',
        provider: '~/plugins/imgproxy-provider.js',
        options: {
          baseURL: 'https://your-domain.imgproxy.net',
          salt: 'your-imgproxy-salt', // Secure URL Salt (optional)
        },
      },
    },
    provider: 'imgproxy', // Default provider
  },
  ...
}
```

Now you can use the custom Imgproxy.net provider:

```html
<nuxt-img src="/your-image.jpg" width="500" />
