t = 0:dt:10;

% Run the simulation
for i=1:length(t)
    % Evaluate the reaction terms
    fy = r*y.*(1-y/K);
    gx = a*x;

    % Evaluate the Laplacian operator
    d2y = del2(y0,dx);
    d2x = del2(x0,dx);

    % Update the population and information densities
    y1 = y0 + dt*(fy + D*d2y);
    x1 = x0 + dt*(gx + C*d2x);

    % Apply zero-flux boundary conditions
    y1(1,:) = y1(2,:);
    y1(n,:) = y1(n-1,:);
    y1(:,1) = y1(:,2);
    y1(:,n) = y1(:,n-1);

    x1(1,:) = x1(2,:);
    x1(n,:) = x1(n-1,:);
    x1(:,1) = x1(:,2);
    x1(:,n) = x1(:,n-1);

    % Update the variables
