            line.set_xy2(uptrend, bar_index - i, high[i])
            startUptrend := i
            startDowntrend := na

    if not na(startUptrend)
        if is_pivot(i, true)
            line.set_xy1(downtrend, bar_index - startDowntrend, low[startDowntrend])
            line.set_xy2(downtrend, bar_index - i, low[i])
            startDowntrend := i
            startUptrend := na

    if na(startUptrend) and is_pivot(i, false)
        startUptrend := i

    if na(startDowntrend) and is_pivot(i, true)
        startDowntrend := i

// Calculate slopes and potential intersection price
float uptrend_slope = na
float downtrend_slope = na
float intersection_price = na
if not na(startUptrend) and not na(startDowntrend)
    uptrend_slope := (high[startUptrend] - high[startUptrend - 1]) / (startUptrend - (startUptrend - 1))
    downtrend_slope := (low[startDowntrend] - low[startDowntrend - 1]) / (startDowntrend - (startDowntrend - 1))
    intersection_price := uptrend_slope * bar_index + (high[startUptrend] - uptrend_slope * startUptrend)

// Calculate and plot trend change signals
trend_change = intersection_price and ta.crossover(intersection_price, line.get_price(downtrend))

plotshape(trend_change, style=shape.triangleup, location=location.belowbar, color=color.green, size=size.small, title="Trend Change Signal")
