        this.oznaka = oznaka;
    }

    public double getUbrzanje() {
        return ubrzanje;
    }

    public void setUbrzanje(double ubrzanje) {
        double g = 9.81;
        this.ubrzanje = g * Math.sin(Math.toRadians(nagib)) + ubrzanje;
    }

    public double izracunajBrzinu(double vp) {
        double s = duzina;
        double ak = ubrzanje;
        double vk = Math.sqrt((2 * ak * s) + (vp * vp));
        return vk;
    }

    public double izracunajVreme(double vp) {
        double vk = izracunajBrzinu(vp);
        double t = (vk - vp) / ubrzanje;
        return t;
    }

    public String opisDeonice() {
        return oznaka + "(" + duzina + "," + nagib + ")";
    }
}

