            if (broj > maxBroj) {
                najcescaOznaka = o;
                maxBroj = broj;
            } else if (broj == maxBroj) {
                if (najcescaOznaka.equals("")) {
                    najcescaOznaka = o;
                }
            }
        }
        if (najcescaOznaka.equals("")) {
            najcescaOznaka = deonice.get(0).getOznaka();
        }
