//转换函数
std::string float_2_hex(float f){
    uint8_t buf[sizeof(float)];
    memcpy(buf, &f, sizeof(float));

    std::ostringstream stream;
    stream << std::hex << std::setfill('0');

    for (uint8_t uc : buf){
        stream << std::setw(2) << static_cast<uint16_t>(uc); 
    }

    return stream.str();
}

