    # Set the starting code based on B, D or G dataframe
    if (k == 2) {
      starting_code <- "B.1.1"
    } else if (k == 4) {
      starting_code <- "D.1.2andD.1.3"
    } else {
      starting_code <- "G.4.1"
    }

