    current_entry <- 1

    # Loop through each row in the df_ dataframe
    for (i in 1:nrow(merge_df)) {
      code <- merge_df[i, 1]
      value <- merge_df[i, 3]

      # Check if a new entry has started
      if (code == starting_code && i > 1) {
