import csv
import requests
import os
import datetime

API_URL = "https://haveibeenpwned.com/api/v3/breachedaccount/{}?truncateResponse=false"

def get_api_data(email):
    headers = {
        "hibp-api-key": "your_api_key_here",
        "user-agent": "your_user_agent_here"
    }
    response = requests.get(API_URL.format(email), headers=headers)
    if response.status_code == 200:
        return response.json()
    return None

def process_csv(input_path, output_path):
    if os.path.exists(output_path):
        output_data = []
        with open(output_path, newline='') as outfile:
            reader = csv.reader(outfile)
            next(reader, None)  # skip header
            for row in reader:
                output_data.append({
                    "source_email": row[0],
                    "source_date_control": row[1],
                    "api_result_name": row[2],
                    "api_result_modifieddate": row[3],
                    "compare_flag": row[4],
                })
    else:
        output_data = []

    with open(input_path, newline='') as infile, open(output_path, "w", newline='') as outfile:
        reader = csv.DictReader(infile)
        fieldnames = ["source_email", "source_date_control", "api_result_name", "api_result_modifieddate", "compare_flag"]
        writer = csv.DictWriter(outfile, fieldnames=fieldnames)
        writer.writeheader()

        for row in reader:
            if row["date_control"]:
                if any(o["source_email"] == row["xxx"] for o in output_data):
                    continue
                email_data = get_api_data(row["xxx"])
                if email_data:
                    for entry in email_data:
                        modified_date = datetime.datetime.strptime(entry["ModifiedDate"], "%Y-%m-%dT%H:%M:%S")
                        date_control = datetime.datetime.strptime(row["date_control"], "%Y-%m-%d %H:%M:%S")
                        compare_flag = modified_date > date_control
                        output_row = {
                            "source_email": row["xxx"],
                            "source_date_control": row["date_control"],
                            "api_result_name": entry["Name"],
                            "api_result_modifieddate": entry["ModifiedDate"],
                            "compare_flag": compare_flag,
                        }
                        writer.writerow(output_row)
                        output_data.append(output_row)
                else:
                    output_row = {
                        "source_email": row["xxx"],
                        "source_date_control": row["date_control"],
                        "api_result_name": "Not found",
                        "api_result_modifieddate": "",
                        "compare_flag": False,
                    }
                    writer.writerow(output_row)
                    output_data.append(output_row)

if __name__ == "__main__":
    process_csv("query_result.csv", "results_with_flag.csv")
