```csharp
public bool UnlockBarn(int barnID, int cost)
{
    if(currentCurrency < cost)
    {
        return false; // Not enough currency to unlock the barn.
    }

    SaveCurrency(currentCurrency - cost); // Deduct the cost from the current currency and save it.
    SaveBarnStatus(barnID, true); // Save the unlocked status of the barn.

    return true; // Successfully unlocked the barn.
}
