```csharp
public int barnID; // Assign a unique ID to each barn instance in the Unity Editor
public int unlockCost; // Assign the cost to unlock this barn in the Unity Editor.
public GameObject door; // Assign the door GameObject of the barn in the Unity Editor.
public GameObject garbage; // Assign the garbage GameObject of the barn in the Unity Editor.
public float doorRotationSpeed = 1.0f;

private bool isUnlocked = false;
private Quaternion initialDoorRotation;
private Quaternion openDoorRotation;

private void Start()
{
    initialDoorRotation = door.transform.rotation;
    openDoorRotation = Quaternion.Euler(initialDoorRotation.eulerAngles + new Vector3(0, 90, 0)); // Assuming a 90-degree rotation around the Y-axis.

    GameManager gameManager = GameObject.FindObjectOfType<GameManager>();
    isUnlocked = gameManager.LoadBarnStatus(barnID);

    if (isUnlocked)
    {
        door.transform.rotation = openDoorRotation;
        garbage.SetActive(false);
    }
}

public void Unlock()
{
    GameManager gameManager = GameObject.FindObjectOfType<GameManager>();

    if (gameManager.UnlockBarn(barnID, unlockCost))
    {
        isUnlocked = true;
        garbage.SetActive(false);
        StartCoroutine(RotateDoor());
    }
}

private IEnumerator RotateDoor()
{
    float timer = 0;

    while (timer < 1)
    {
        timer += Time.deltaTime * doorRotationSpeed;
        door.transform.rotation = Quaternion.Lerp(initialDoorRotation, openDoorRotation, timer);
        yield return null;
    }
}
```

