```sql
CREATE OR REPLACE FUNCTION extract_age (p_input VARCHAR2)
   RETURN VARCHAR2
IS
   v_age          NUMBER;
   v_unit         CHAR(1);
   v_output         VARCHAR2 (50);
BEGIN
   -- 提取年龄数值和单位
   SELECT TO_NUMBER (REGEXP_SUBSTR (p_input, '\d+')) AS age,
          REGEXP_SUBSTR (p_input, '[岁月天时]') AS unit
     INTO v_age, v_unit
     FROM DUAL;

   -- 设置优先级，确定最大年龄单位
   IF INSTR (p_input, '岁') > 0
   THEN
      -- 如果包含岁，单位设置为岁
      v_output := TO_CHAR (v_age) || '岁';
   ELSIF INSTR (p_input, '月') > 0
   THEN
      -- 如果包含月，并且没有岁，单位设置为月
      v_output := TO_CHAR (v_age) || '月';
   ELSIF INSTR (p_input, '天') > 0
   THEN
      -- 如果包含天，并且没有岁和月，单位设置为天
      v_output := TO_CHAR (v_age) || '天';
   ELSE
      -- 如果只包含时，单位设置为时
      v_output := TO_CHAR (v_age) || '时';
   END IF;

   RETURN v_output;
END;
/

-- Test cases
DECLARE
   v_input  VARCHAR2 (50);
   v_output VARCHAR2 (50);
BEGIN
   v_input := '22岁5月1天';
   v_output := extract_age (v_input);
   DBMS_OUTPUT.PUT_LINE ('Input: ' || v_input || ' Output: ' || v_output);

   v_input := '9月22天';
   v_output := extract_age (v_input);
   DBMS_OUTPUT.PUT_LINE ('Input: ' || v_input || ' Output: ' || v_output);

   v_input := '1天';
   v_output := extract_age (v_input);
   DBMS_OUTPUT.PUT_LINE ('Input: ' || v_input || ' Output: ' || v_output);
END;
/
```

