            dynamic data = JsonConvert.DeserializeObject(json);

            // 创建多边形（polygon）的画布元素
            foreach (var points in data.shape.polygons)
            {
                var polygon = new Polygon();
                var pointCollection = new PointCollection();

                // 将多边形的点坐标添加到PointCollection中
                for (int i = 0; i < points.Count; i += 2)
                {
                    var x = points[i];
                    var y = points[i + 1];
                    pointCollection.Add(new Point(x, y));
                }

                // 设置多边形属性
                polygon.Points = pointCollection;
                polygon.Stroke = Brushes.Black;
                polygon.Fill = Brushes.Transparent;

                // 将多边形加入canvas
                canvas.Children.Add(polygon);
            }

            // 创建圆形（outlineCircle）的画布元素
            foreach (var circle in data.shape.outlineCircle)
            {
                var ellipse = new Ellipse();

                // 设置圆形属性
                ellipse.Width = circle[2] * 2;
                ellipse.Height = circle[2] * 2;
                Canvas.SetLeft(ellipse, circle[0] - circle[2]);
                Canvas.SetTop(ellipse, circle[1] - circle[2]);
                ellipse.Stroke = Brushes.Black;
                ellipse.Fill = Brushes.Transparent;

                // 将圆形加入canvas
                canvas.Children.Add(ellipse);
