```
using SpreadsheetLight;

class Program
{
    static void Main(string[] args)
    {
        // 打开现有的Excel电子表格
        SLDocument sl = new SLDocument("example.xlsx");

        // 选择要操作的工作表
        SLWorksheet worksheet = sl.SelectWorksheet("Sheet1");

        // 定义要遍历的区域 (A1:B3)
        string region = "A1:B3";

        // 获取区域范围
        SLRange range = worksheet.Ranges[region];

        // 读取每一行的数据，并在控制台上输出
        for (int r = range.StartRowIndex; r <= range.EndRowIndex; r++)
        {
            string rowData = "";

            for (int c = range.StartColumnIndex; c <= range.EndColumnIndex; c++)
            {
                string cellAddress = SLConvert.ToCellReference(c, r);
                string cellValue = worksheet.Cells[cellAddress].Text;
                rowData += cellValue + ",";
            }

            Console.WriteLine(rowData.TrimEnd(','));
        }

        // 将新数据写入每一行，值为行号
        for (int r = range.StartRowIndex; r <= range.EndRowIndex; r++)
        {
            for (int c = range.StartColumnIndex; c <= range.EndColumnIndex; c++)
            {
                string cellAddress = SLConvert.ToCellReference(c, r);
                worksheet.SetCellValue(cellAddress, (r - range.StartRowIndex + 1));
            }
        }

        // 保存更改后的Excel文件
        sl.SaveAs("example_modified.xlsx");

        // 关闭Excel文件
        sl.CloseWithoutSaving();
    }
}
```

