如果需要多级并且有checkbox，可以在el-option的子元素中再次使用el-option嵌套来实现多级列表，使用el-checkbox来实现选框。代码如下：

<template>
  <el-select
    v-model="selectedValues"
    placeholder="请选择"
    clearable
    :multiple="true"
    @clear="clearSelectedValues"
    class="select-group"
  >
    <el-option v-for="option in options" :key="option.value" :label="option.label" :value="option.value">
      <el-checkbox-group size="small">
        <el-checkbox v-model="option.checked" :label="option.value" @change="handleCheckChange"></el-checkbox>
      </el-checkbox-group>
      <template v-if="option.children">
        <el-option v-for="child in option.children" :key="child.value" :label="child.label" :value="child.value">
          <el-checkbox-group size="small">
            <el-checkbox v-model="child.checked" :label="child.value" @change="handleCheckChange"></el-checkbox>
          </el-checkbox-group>
          <template v-if="child.children">
            <el-option v-for="grandChild in child.children" :key="grandChild.value" :label="grandChild.label" :value="grandChild.value">
              <el-checkbox-group size="small">
                <el-checkbox v-model="grandChild.checked" :label="grandChild.value" @change="handleCheckChange"></el-checkbox>
              </el-checkbox-group>
            </el-option>
          </template>
        </el-option>
      </template>
    </el-option>
  </el-select>
</template>

<script>
import { option } from "./option";

export default {
  data() {
    return {
      selectedValues: [],
      options: option.map(item => {
        return {
          label: item.label,
          value: item.value,
          checked: false,
          children: item.children
            ? item.children.map(child => {
                return {
                  label: child.label,
                  value: child.value,
                  checked: false,
                  children: child.children
                    ? child.children.map(grandChild => {
                        return {
                          label: grandChild.label,
                          value: grandChild.value,
                          checked: false
                        }
                      })
                    : []
                }
              })
            : []
        }
      })
    };
  },
  methods: {
    clearSelectedValues() {
      this.selectedValues = [];
      this.checkAll(false);
    },
    handleCheckChange() {
      this.setSelectedValues();
    },
    setSelectedValues() {
      const selectedValues = [];
      this.options.forEach(option => {
        if (option.checked) {
          selectedValues.push(option.value);
        }
        if (option.children) {
          option.children.forEach(child => {
            if (child.checked) {
              selectedValues.push(child.value);
            }
            if (child.children) {
              child.children.forEach(grandChild => {
                if (grandChild.checked) {
                  selectedValues.push(grandChild.value);
                }
              });
            }
          });
        }
      });
      this.selectedValues = selectedValues;
    },
    checkAll(value) {
      this.options.forEach(option => {
        option.checked = value;
        if (option.children) {
          option.children.forEach(child => {
            child.checked = value;
            if (child.children) {
              child.children.forEach(grandChild => {
                grandChild.checked = value;
              });
            }
          });
        }
      });
    },
  },
};
</script>

