```java
@Service
public class AutocompleteServiceImpl implements AutocompleteService {

  @Autowired
  private AutocompleteRepository repository;

  @Autowired
  private RedisTemplate redisTemplate;

  @Override
  public List<String> getAutocomplete(String inputValue) {
    // 先尝试从缓存中获取值
    String cacheKey = "autocomplete-" + inputValue;
    List<String> result = redisTemplate.opsForList().range(cacheKey, 0, -1);
    if (result == null || result.isEmpty()) {
      // 如果缓存中不存在，则从数据库中查询
      result = repository.findByKeywordStartingWith(inputValue);
      // 将查询结果保存到缓存中
      redisTemplate.opsForList().rightPushAll(cacheKey, result);
      // 设置缓存过期时间（30分钟）
      redisTemplate.expire(cacheKey, 30, TimeUnit.MINUTES);
    }
    return result;
  }
}
