@Service
public class AutocompleteServiceImpl implements AutocompleteService {

  @Autowired
  private AutocompleteRepository autocompleteRepository;

  @Autowired
  private RedisTemplate<String, List<AutocompleteInfo>> redisTemplate;

  @Override
  public List<String> getAutocomplete(String keyword) {
    if (keyword.length() < 2) {
      return Collections.emptyList();
    }

    final String cacheKey = "autocomplete";

    List<AutocompleteInfo> cacheValue = redisTemplate.opsForValue().get(cacheKey);
    if (cacheValue == null) {
      cacheValue = autocompleteRepository.findAll();
      redisTemplate.opsForValue().set(cacheKey, cacheValue, 1, TimeUnit.DAYS);
    }

    return cacheValue.stream()
                     .filter(info -> info.getKeyword().toLowerCase().contains(keyword.toLowerCase()))
                     .map(AutocompleteInfo::getKeyword)
                     .collect(Collectors.toList());
  }
}
