const pptFileReader = new FileReader();
pptFileReader.onload = (e) => {
    // 使用Office Web Viewer展示ppt文件预览
    const pptBlob = new Blob([e.addEventListener.target.result], {type: "application/vnd.openxmlformats-officedocument.presentationml.presentation"});
    pwdPromoteMSG.hide();
