        webViewerFrame.setAttribute("width", "100%");
        webViewerFrame.setAttribute("height", "100%");
        webViewerFrame.setAttribute("frameborder", "0");
        webViewerFrame.setAttribute("src", viewerSrc + pptBlob);
        document.getElementById("ppt-preview-wrapper").appendChild(webViewerFrame);
    } else {
        webViewerFrame.contentWindow.location.replace(viewerSrc + pptBlob);
    }
};
pptFileReader.onerror = () => {
    pwdPromoteMSG.hide();
