    <p class="text-center mt-3" v-show="products.length === 0 && !isLoading">暂无数据</p>
  </div>
</template>

<script>
import { Input, Button, List } from 'ant-design-vue';
export default {
  components: {
    'a-input': Input,
    'a-button': Button,
    'a-list': List,
    'a-list-item': List.Item,
    'a-list-item-meta': List.Item.Meta,
  },
  data() {
    return {
      keyword: '',
      products: [],
      isLoading: false,
    };
  },
  methods: {
    search() {
      if (!this.keyword) {
        return;
      }

      this.isLoading = true; // 开始加载

      // 使用 axios 发起异步请求
      axios.get('url.list', { params: { keyword: this.keyword } })
        .then((response) => {
          this.products = response.data;
          this.isLoading = false; // 完成加载
        })
        .catch(() => {
          this.isLoading = false; // 完成加载
        });
    },
  },
};
</script>

