import LineChart from '@/components/LineChart.vue'
import axios from 'axios'

export default {
  name: 'RealtimeData',
  components: {
    LineChart
  },
  data () {
    return {
      deviceName: 'ABC设备',
      chartData: {
        labels: [],
        datasets: [
          {
            label: '温度',
            data: [],
            fill: false,
            borderColor: '#4BC0C0'
          },
          {
            label: '湿度',
            data: [],
            fill: false,
            borderColor: '#FFCE56'
          }
        ]
      }
    }
  },
  mounted () {
    const ws = new WebSocket('ws://localhost:8080/api/realtime') // WebSocket连接
    ws.onmessage = (event) => {
      const data = JSON.parse(event.data) // 解析接收到的数据
      // 将数据添加到图表的数据集中
      this.chartData.labels.push(data.timestamp)
      this.chartData.datasets[0].data.push(data.temperature)
      this.chartData.datasets[1].data.push(data.humidity)
    }

    // Ajax请求
    axios.get('http://localhost:5000/api/device/latest') // Web API地址
      .then(response => {
        const deviceData = response.data
        // 初始化图表的数据
        this.chartData.labels = deviceData.map(d => d.timestamp)
        this.chartData.datasets[0].data = deviceData.map(d => d.temperature)
        this.chartData.datasets[1].data = deviceData.map(d => d.humidity)
      })
      .catch(error => console.log(error))
  }
}
</script>
```

