<script>
import { reactive, onMounted, onUnmounted } from 'vue'
import { io } from 'socket.io-client'

export default {
  name: 'DeviceMonitor',
  props: {
    deviceId: String
  },
  setup (props) {
    const device = reactive({
      name: props.deviceId,
      temperature: null,
      humidity: null,
      pressure: null
    })

    let socket

    const connect = () => {
      socket = io('http://localhost:5000')
      socket.on('connect', () => {
        socket.emit('device', props.deviceId)
      })
      socket.on('data', (data) => {
        if (data.device === device.name) {
          device.temperature = data.temperature
          device.humidity = data.humidity
          device.pressure = data.pressure
        }
      })
    }

    const disconnect = () => {
      if (socket) {
        socket.disconnect()
      }
    }

    onMounted(() => {
      connect()
    })

    onUnmounted(() => {
      disconnect()
    })

    return {
      device,
      connect,
      disconnect
    }
  }
}
</script>

