using Microsoft.AspNetCore.Builder;
using Microsoft.AspNetCore.Hosting;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Hosting;
using System.Net.WebSockets;
using System.Text.Json;
using System.Threading;
using System.Threading.Tasks;

namespace DeviceMonitor
{
    public class Startup
    {
        public void ConfigureServices(IServiceCollection services)
        {
            services.AddControllers();
        }

        public void Configure(IApplicationBuilder app, IWebHostEnvironment env)
        {
            app.UseRouting();

            app.UseEndpoints(endpoints =>
            {
                endpoints.MapControllerRoute(
                    name: "default",
                    pattern: "{controller}/{action}/{id?}");
            });

            app.UseWebSockets();

            app.Use(async (context, next) =>
            {
                if (context.WebSockets.IsWebSocketRequest)
                {
                    var socket = await context.WebSockets.AcceptWebSocketAsync();
                    var deviceId = await ReceiveDeviceId(socket);
                    await MonitorDevice(deviceId, socket);
                }
                else
                {
                    await next();
                }
            });
        }

        private static async Task<string> ReceiveDeviceId(WebSocket socket)
        {
            var buffer = new byte[4096];
            var result = await socket.ReceiveAsync(new ArraySegment<byte>(buffer), CancellationToken.None);
            return JsonSerializer.Deserialize<string>(Encoding.UTF8.GetString(buffer, 0, result.Count));
        }

        private static async Task MonitorDevice(string deviceId, WebSocket socket)
        {
            var client = new TdEngineClient();
            await client.OpenAsync("localhost", 6020, "root", "taosdata");
            await client.UseDatabaseAsync("test");
            var statement = $"select last temperature,humidity,pressure from {deviceId}";

            var buffer = new byte[4096];
            var result = await client.QueryAsync(statement);
            var data = JsonSerializer.Deserialize<DeviceData>(result.Read().ToString());

            while (socket.State == WebSocketState.Open)
            {
                await socket.SendAsync(new ArraySegment<byte>(
                    Encoding.UTF8.GetBytes(JsonSerializer.Serialize(data))), WebSocketMessageType.Text, true, CancellationToken.None);

                await Task.Delay(1000);

                result = await client.QueryAsync(statement);
                data = JsonSerializer.Deserialize<DeviceData>(result.Read().ToString());
            }

            await client.CloseAsync();
        }
    }

