```javascript
// Initialize Firebase
const firebaseConfig = {
  apiKey: "YOUR_API_KEY",
  authDomain: "your-auth-domain",
  databaseURL: "your-database-url",
  projectId: "your-project-id",
  storageBucket: "your-storage-bucket",
  messagingSenderId: "your-messaging-sender-id",
  appId: "your-app-id",
};
firebase.initializeApp(firebaseConfig);

const db = firebase.database();
const infoLabels = {
  accelerometer: {
    x: document.getElementById("accelerometer-x"),
    y: document.getElementById("accelerometer-y"),
    z: document.getElementById("accelerometer-z"),
  },
  gyroscope: {
    alpha: document.getElementById("gyroscope-alpha"),
    beta: document.getElementById("gyroscope-beta"),
    gamma: document.getElementById("gyroscope-gamma"),
  },
};

db.ref("sensors").on("value", (snapshot) => {
  const data = snapshot.val();

  if (data) {
    Object.entries(data).forEach(([sensor, values]) => {
      if (infoLabels[sensor]) {
        Object.entries(values).forEach(([axis, value]) => {
          const label = infoLabels[sensor][axis];
          if (label) {
            label.textContent = value;
          }
        });
      }
    });
  }
});
```

