type Props = {
  readonly upgrade: Upgrade;
  readonly brbs: number;
  readonly setBRBs: (value: React.SetStateAction<number>) => void;
  readonly upgradeCounts: Map<Upgrade, number>;
  readonly setUpgradeCounts: (
    value: React.SetStateAction<Map<Upgrade, number>>
  ) => void;
};

const UpgradeDisplay = ({
  upgrade,
  brbs,
  setBRBs,
  upgradeCounts,
  setUpgradeCounts,
}: Props) => {
  const purchasedCount = upgradeCounts.get(upgrade)!;
  const price = upgrade.basePrice * (1 + purchasedCount * upgrade.priceIncreaseRate);

  const buyUpgrade = () => {
    if (brbs >= price) {
      setBRBs(brbs - price);
      setUpgradeCounts((prevState) =>
        new Map(prevState).set(upgrade, purchasedCount + 1)
      );
    }
  };

  return (
    // ... (no changes here)
    <button onClick={buyUpgrade} disabled={brbs < price}>
      Buy
    </button>
  );
};
