const Game = () => {
  const [brbs, setBRBs] = useState(0);
  const [upgradeCounts, setUpgradeCounts] = useState(initUpgrades());

  const clickIncome =
    1 +
    Array.from(upgradeCounts)
      .map(([upgrade, count]) => upgrade.incomePerClick * count)
      .reduce((a, b) => a + b, 0);

  const tickIncome = Array.from(upgradeCounts)
    .map(([upgrade, count]) => upgrade.incomePerTick * count)
    .reduce((a, b) => a + b, 0);

  const win = Array.from(upgradeCounts).every(([_, count]) => count >= 1);

  return (
    <div className={styles.container}>
      <h1>{win ? "??BRB Clicker??" : "BRB Clicker"}</h1>
      {win && <p>YOU WON! See how far you go!</p>}
