      if (selected.parent().is(':first-child')) {
        selected.blur();
        link.focus();
      } else {
        selected.parent().prev().find('a').focus();
      }
    } else if (e.keyCode === 40) { // Down arrow
      e.preventDefault();
      if (selected.parent().is(':last-child')) {
        selected.blur();
        link.focus();
