  <div class="bookmarklet-section">
    <input type="text" placeholder="Bookmarklet name">
    <input type="text" placeholder="Bookmarklet code">
    <input type="text" placeholder="Bookmarklet description">
    <button onclick="removeBookmarkletSection(this)">Remove</button>
  </div>
</div>
<button onclick="addBookmarkletSection()">Add bookmarklet</button>
<button id="generate-btn" onclick="generatePage()">Generate page</button>
<a id="generated-link" class="hidden">Bookmarklets</a>

<script>
function addBookmarkletSection() {
  const bookmarkletSections = document.getElementById("bookmarklet-sections");
  const newBookmarkletSection = document.createElement("div");
  newBookmarkletSection.classList.add("bookmarklet-section");
  newBookmarkletSection.innerHTML = `
    <input type="text" placeholder="Bookmarklet name">
    <input type="text" placeholder="Bookmarklet code">
    <input type="text" placeholder="Bookmarklet description">
    <button onclick="removeBookmarkletSection(this)">Remove</button>
  `;
  bookmarkletSections.appendChild(newBookmarkletSection);
}

function removeBookmarkletSection(button) {
  const bookmarkletSection = button.parentNode;
  bookmarkletSection.parentNode.removeChild(bookmarkletSection);
}

function generatePage() {
  const bookmarkletSections = document.querySelectorAll(".bookmarklet-section");
  let html = "<!DOCTYPE html> <html> <head> <meta charset='UTF-8'> <title>Bookmarklets</title> </head> <body> <ul>";

  for (let i = 0; i < bookmarkletSections.length; i++) {
    const name = bookmarkletSections[i].querySelector("input").value;
    const code = bookmarkletSections[i].querySelectorAll("input")[1].value;
    const description = bookmarkletSections[i].querySelectorAll("input")[2].value;
    html += `<li><a href="javascript:${code}">${name}</a>: ${description}</li>`;
  }

  html += "</ul> </body> </html>";

  const link = document.getElementById("generated-link");
  link.href = "data:text/html," + encodeURIComponent(html);
  link.classList.remove("hidden");
}
</script>
</body>
