#include <iostream>
#include <algorithm>
#include <bitset>
#include <regex>
using namespace std;

bool is_binary(string s) {
    regex binary_regex("^[01]+$"); // регулярное выражение для проверки на двоичное число
    return regex_match(s, binary_regex);
}

string to_binary(int n) {
    return bitset<32>(n).to_string(); // преобразование числа в двоичную строку
}

int to_decimal(string s) {
    return bitset<32>(s).to_ulong(); // преобразование двоичной строки в число
}

int main() {
    int n; // n - размер массивадвоичных чисел
    cout << "Введите размер массива: ";
    cin >> n;
    int a[n]; // объявление массива размером n
    string input;
    int sum = 0; // переменная для хранения суммы чисел
    cout << "Введите двоичные числа массива: ";
    for(int i = 0; i < n; i++) {
        cin >> input;
        while(!is_binary(input)) { // повторять ввод, пока не будет введено двоичное число
            cout << "Некорректный ввод. Введите двоичное число: ";
            cin >> input;
        }
        a[i] = to_decimal(input); // преобразование двоичной строки в число
        sum += a[i]; // добавление очередного числа к сумме
    }
    cout << "Сумма чисел: " << to_binary(sum) << endl; // преобразование суммы в двоичную строку и вывод на экран
    // сортировка элементов массива по убыванию
    sort(a, a + n, greater<int>());
    cout << "Упорядоченный массив: ";
    // вывод упорядоченного массива на экран
    for(int i = 0; i < n; i++) {
        cout << to_binary(a[i]) << " ";
    }
    cout << endl;
    return 0;
}
