                </List.Item>
              )}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card title="我的历史" extra={<HistoryOutlined />} bordered={false}>
            <List
              itemLayout="horizontal"
              dataSource={data}
              renderItem={item => (
                <List.Item>
                  <List.Item.Meta
                    title={item.title}
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card title="我的关注" extra={<StarOutlined />} bordered={false}>
            <List
              itemLayout="horizontal"
              dataSource={data}
              renderItem={item => (
                <List.Item>
                  <List.Item.Meta
                    title={item.title}
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card title="我的浏览历史" extra={<UserOutlined />} bordered={false}>
            <List
              itemLayout="horizontal"
              dataSource={data}
              renderItem={item => (
                <List.Item>
                  <List.Item.Meta
                    title={item.title}
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>
      </Row>
      <Row gutter={[16, 16]}>
        <Col xs={24} sm={12} md={8}>
          <Card title="新闻" extra={<LineChartOutlined />} className="custom-card">
            <List
              itemLayout="horizontal"
              dataSource={news}
              renderItem={item => (
                <List.Item>
                  <List.Item.Meta
                    title={item.title}
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={8}>
          <Card title="动态追踪" extra={<LineChartOutlined />} className="custom-card">
            <List
              itemLayout="horizontal"
              dataSource={dynamicTracking}
              renderItem={item => (
                <List.Item>
                  <List.Item.Meta
                    title={item.title}
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={8}>
          <Card title="交易日历" extra={<CalendarOutlined />} className="custom-card">
            <Calendar dateCellRender={dateCellRender} />
          </Card>
        </Col>
      </Row>
    </div>
  );
}

export default App;
