import { Box, Grid } from "@mui/material";
import { useEffect, useState } from "react";
import {
    PieChart,
    Pie,
    Legend,
    Cell,
    Tooltip,
    ResponsiveContainer,
    Label
} from "recharts";

interface CountBySymbolsWidgetType {
    count: number
    symbol: string
}

interface CountBySymbolsWidgetProps {
    data: CountBySymbolsWidgetType[]
}

interface BulletPropsType {
    backgroundColor: string
    size: string
}

interface CustomLabelType {
    viewBox?: any
    countBySymbols: CountBySymbolsWidgetType[]
}

const COLORS = ["#AFAFAF", "#9C9C9C", "#8F8F8F", "#7F7F7F", "#606060", "#000"]

const CountBySymbolsWidget = ({ }: CountBySymbolsWidgetProps) => {   

    const [data, setData] = useState([
        {count: 6, symbol: 'BTCUSDT'}
    ]);

    // добавляем новые объекты в массив data
    useEffect(() => {
      if(data.length < 60) {



          const newData = new Array(59).fill({ count: 10, symbol: "ETHUSDT" });
          setData(prevData => [...prevData, ...newData]);
        }
    }, [data])

    const countBySymbols = data.sort((a, b) => (a.count > b.count) ? -1 : (a.count < b.count) ? 1 : 0)

    const CustomTooltip = ({ payload }: any) => {
        return (
            <Box bgcolor="#21201F" px={2} height={32} display="flex" alignItems="center" borderRadius={5} >
                <text style={{ color: "#fff", fontWeight: "300", fontSize: "small" }} >{payload?.[0]?.payload?.symbol} </text>
                <text style={{ color: "#9C9C9C", fontWeight: "300", fontSize: "small", marginLeft: 4 }}>${payload?.[0]?.value}</text>
            </Box>
        );
    };


    const percentStep = 100 / COLORS.length;
    const segmentSize = (countBySymbols.length * percentStep) / 100

    const getColor = (index: number) => {
        if (index < segmentSize) {
            return COLORS[0];
        } else if (index < segmentSize * 2) {
            return COLORS[1];
        } else if (index < segmentSize * 3) {
            return COLORS[2];
        } else if (index < segmentSize * 4) {
            return COLORS[3];
        } else if (index < segmentSize * 5) {
            return COLORS[4];
        } else {
            return COLORS[5];
        }
    };
    return (
        <Grid style={{ width: "100%", height: 250 }}>
            <ResponsiveContainer>
                <PieChart style={{ display: "flex" }} >
                    <Pie
                        data={countBySymbols}
                        labelLine={false}
                        dataKey="count"
                        cx={120}
                        cy={120}
                        innerRadius={88}
                        outerRadius={115}
                    >
                        {countBySymbols.map((entry, index) => (
                            <Cell key={`cell-${index}`} fill={getColor(index)} />
                        ))}
                        <Label content={<CustomLabel countBySymbols={countBySymbols} />}  />
                    </Pie>
                    <Tooltip content={<CustomTooltip />} />
                    <Legend content={<CustomizedLegend count />} />
                </PieChart>
            </ResponsiveContainer>
        </Grid>
    );
}

const Bullet = ({ backgroundColor, size }: BulletPropsType) => (
    <Grid style={{
        backgroundColor,
        width: size,
        height: size,
        borderRadius: 35
    }}></Grid>
)

const CustomizedLegend = (props: any) => {
    const { payload } = props;

