import { useMemo, useState } from 'react';
import { useTranslation } from 'react-i18next';
import styled from '@emotion/styled';
import { Button, Fade } from '@mui/material';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import { isWithinInterval } from 'date-fns';
import { MouseEventHandler } from '@nivo/pie';
import { useNavigate } from 'react-router-dom';
import queryString from 'query-string';
import {
  Card,
  CardState,
  formatDateLocale,
  groupDataByKey,
  isNumber,
} from '@cbe/ui';
import type { NodeMouseEventHandler } from '@nivo/treemap';

import { NivoPieChart, NivoPieChartData } from './NivoPieChart';
import { NivoTreeMapChart, NivoTreeMapData } from './NivoTreeMapChart';
import { formatTreeMapData } from '../../functions/formatTreeMapData';

import type { useFinancialSalesData } from '../../hooks/useFinancialSalesData';
import type { SearchPeriodDates } from '../../functions/getPeriod';
import type { ActiveFilters } from '../../hooks/useSearchFilters';

const ChartContainer = styled.div`
  width: 100%;
  height: 100%;
  position: relative;

  .totals-container {
    width: 100%;
    height: 100%;
    position: absolute;
  }
  .pie-container {
    width: 100%;
    height: 100%;
    position: absolute;
  }

  transition: opacity 1s;
  &.totals {
    .pie-container {
      opacity: 0;
      pointer-events: none;
    }
  }
  :not(&.totals) {
    .totals-container {
      opacity: 0;
      pointer-events: none;
    }
  }
`;

export type SelectedKey =
  | 'discount'
  | 'noSale'
  | 'reduced'
  | 'refund'
  | 'void'
  | 'voidAll';

type ChartData = {
  id: string;
  label: string;

  discount: { count: number; value: number };
  noSale: { count: number };
  reduced: { count: number; value: number };
  refund: { count: number; value: number };
  voidAll: { count: number; value: number };
  void: { count: number; value: number };
};

function getFilterValue(key: SelectedKey) {
  switch (key) {
    case 'discount':
      return ['DISCA##', 'DISCO##', 'DISCP##', 'MARKDOWN##'];
    case 'noSale':
      return 'NOSALE##';
    case 'reduced':
      return 'PriceOverride##';
    case 'refund':
      return 'REFUND##';
    case 'void':
      return 'VOID##';
    case 'voidAll':
      return 'VOIDA##';
  }
}

function addIfNumber(value: number) {
  if (isNumber(value)) {
    return value;
  }
  return 0;
}

export const JournalsStatusChart = ({
  id,
  financialSalesData,
  filters,
  retryData,
  period,
}: {
  id: string;
  financialSalesData: ReturnType<typeof useFinancialSalesData>;
  filters: ActiveFilters['filters'];
  retryData: () => void;
  period: SearchPeriodDates;
}): JSX.Element => {
  const { t } = useTranslation(['common', 'journals']);
  const navigate = useNavigate();

  const [selectedType, setSelectedType] = useState<{
    key: SelectedKey | 'totals';
    data: NivoPieChartData[];
  }>({
    key: 'totals',
    data: [],
  });

  const graphData = useMemo(() => {
    const groupedByStore: ChartData[] = [];

    const filteredSalesData = filters.id
      ? financialSalesData.data.filter(
          (_finData) => filters.id?.includes(_finData.storeId) || false
        )
      : financialSalesData.data;

    const groupedSalesData = groupDataByKey(filteredSalesData, 'storeId');

    let totalDiscountCount = 0;
    let totalNoSaleCount = 0;
    let totalReducedCount = 0;
    let totalRefundCount = 0;
    let totalVoidAllCount = 0;
    let totalVoidCount = 0;

    for (const [storeId, data] of Object.entries(groupedSalesData)) {
      const filteredData = data.filter((_filData) => {
        if (
          isWithinInterval(new Date(_filData.salesDate), {
            start: period.currentPeriod.start,
            end: period.currentPeriod.end,
          })
        ) {
          return true;
        }
        return false;
      });

      let discountCount = 0;
      let discountValue = 0;
      let noSaleCount = 0;
      let reducedCount = 0;
      let reducedValue = 0;
      let refundCount = 0;
      let refundValue = 0;
      let voidAllCount = 0;
      let voidAllValue = 0;
      let voidCount = 0;
      let voidValue = 0;

      // Get group totals
      for (const _filteredData of filteredData) {
        discountCount += addIfNumber(_filteredData.discountCount);
        discountValue += addIfNumber(_filteredData.discountValue);
        noSaleCount += addIfNumber(_filteredData.noSaleCount);
        reducedCount += addIfNumber(_filteredData.reducedCount);
        reducedValue += addIfNumber(_filteredData.reducedValue);
        refundCount += addIfNumber(_filteredData.refundCount);
        refundValue += addIfNumber(_filteredData.refundValue);
        voidAllCount += addIfNumber(_filteredData.voidAllCount);
        voidAllValue += addIfNumber(_filteredData.voidAllValue);
        voidCount += addIfNumber(_filteredData.voidCount);
        voidValue += addIfNumber(_filteredData.voidValue);
      }

      // Hold onto totals for summary chart
      totalDiscountCount += discountCount;
      totalNoSaleCount += noSaleCount;
      totalReducedCount += reducedCount;
      totalRefundCount += refundCount;
      totalVoidAllCount += voidAllCount;
      totalVoidCount += voidCount;

      groupedByStore.push({
        id: storeId,
        label: data[0]?.storeName || storeId,

        discount: { count: discountCount, value: discountValue },
        noSale: { count: noSaleCount },
        reduced: { count: reducedCount, value: reducedValue },
        refund: { count: refundCount, value: refundValue },
        voidAll: { count: voidAllCount, value: voidAllValue },
        void: { count: voidCount, value: voidValue },
      });
    }

    return {
      groupedByStore,
      totals: formatTreeMapData(
        t,
        totalDiscountCount,
        totalNoSaleCount,
        totalReducedCount,
        totalRefundCount,
        totalVoidAllCount,
        totalVoidCount
      ),
    };
  }, [
    t,
    financialSalesData.data,
    period.currentPeriod.start,
    period.currentPeriod.end,
    filters.id,
  ]);

  const handleTileSegmentClick: NodeMouseEventHandler<NivoTreeMapData> = (
    data
  ) => {
    if (data.data.id !== 'totals') {
      const key = data.data.id;

      setSelectedType({
        key: key,
        data: graphData.groupedByStore.map((store) => {
          return {
            id: store.id,
            label: store.label,
            value: (store[key] as { value?: number }).value,
            quantity: (store[key] as { count: number }).count,
          };
        }),
      });
    }
  };

  function clearSelectedType() {
    setSelectedType({
      key: 'totals',
      data: [],
    });
  }

  const handlePieSegmentClick: MouseEventHandler<
    NivoPieChartData,
    SVGPathElement
  > = (data) => {
    const search = queryString.stringify({
      dateFrom:
        period.selectedPeriod !== 'today'
          ? formatDateLocale(
              period.currentPeriod.start,
              "yyyy_MM_dd_'T'_HH_mm_ss"
            )
          : undefined,
      dateTo:
        period.selectedPeriod !== 'today'
          ? formatDateLocale(
              period.currentPeriod.end,
              "yyyy_MM_dd_'T'_HH_mm_ss"
            )
          : undefined,
      sites: data.id,
      transactions: getFilterValue(selectedType.key as SelectedKey),
    });

    navigate(`/Journals/Data?${search}`);
  };

  return (
    <Fade in>
      <Card
        id={`${id}-card`}
        title={t(`journals:hub-view.pie-chart.label_${selectedType.key}`)}
        titleActions={
          selectedType.key === 'totals' ? undefined : (
            <Button startIcon={<ArrowBackIcon />} onClick={clearSelectedType}>
              {t('journals:hub-view.pie-chart.return-to-totals')}
            </Button>
          )
        }
      >
        <CardState
          error={
            financialSalesData.error
              ? t(`common:error.${financialSalesData.error}`)
              : graphData.groupedByStore.length === 0
              ? t('common:response.no-data')
              : undefined
          }
          handleRetry={financialSalesData.error ? () => retryData() : undefined}
          height="500px"
          isLoading={financialSalesData.isLoading}
        >
          <ChartContainer className={selectedType.key}>
            <div className="totals-container">
              <NivoTreeMapChart
                data={graphData.totals}
                onClick={handleTileSegmentClick}
              />
            </div>

            <div className="pie-container">
              <NivoPieChart
                data={selectedType.data}
                displayValue={
                  selectedType.key === 'noSale' ? 'quantity' : 'value'
                }
                onClick={handlePieSegmentClick}
              />
            </div>
          </ChartContainer>
        </CardState>
      </Card>
    </Fade>
  );
};

