    private bool isAggro = false; // flag to indicate if the enemy is aggroed on the player

    void Start()
    {
        player = GameObject.FindWithTag("Player");
    }

    void Update()
    {
        if (player == null)
        {
            return; // if the player game object doesn't exist, exit the function
        }

        float distanceToPlayer = Vector3.Distance(transform.position, player.transform.position);

        if (distanceToPlayer <= aggroRange)
        {
            isAggro = true;
        }
        else
        {
            isAggro = false;
        }

        if (isAggro)
        {
            transform.LookAt(player.transform); // look at the player

            // move towards the player
            transform.position += transform.forward * speed * Time.deltaTime;

            // attack the player if close enough
            if (distanceToPlayer <= 1.5f)
            {
                PlayerHealth playerHealth = player.GetComponent<PlayerHealth>();
                if (playerHealth != null)
                {
                    playerHealth.TakeDamage(damage);
                }
            }
        }
    }

    public void TakeDamage(int damage)
    {
        health -= damage;
        if (health <= 0)
        {
            Die();
        }
    }

    void Die()
    {
        // play death animation and sound effect, and remove enemy game object from scene
        Destroy(gameObject);
    }
}
```

