```
using System.Collections.Generic;
using UnityEngine;

public class Inventory : MonoBehaviour
{
    public int inventorySize = 20; // the number of items the inventory can hold
    public GameObject inventoryUI; // the UI object representing the inventory
    public GameObject itemSlotPrefab; // the prefab for the inventory slots

    private List<InventoryItem> items = new List<InventoryItem>(); // a list to hold the items in the inventory
    private List<GameObject> itemSlots = new List<GameObject>(); // a list to hold the UI slots for the inventory

    void Start()
    {
        // create the inventory UI slots
        for (int i = 0; i < inventorySize; i++)
        {
            GameObject slot = Instantiate(itemSlotPrefab, inventoryUI.transform);
            itemSlots.Add(slot);
        }
    }

    public void AddItem(InventoryItem item)
    {
        // check if the item is stackable
        if (item.stackable)
        {
            // loop through the existing items in the inventory to see if we can stack the new item
            foreach (InventoryItem currentItem in items)
            {
                if (currentItem.id == item.id && currentItem.count < currentItem.maxCount)
                {
                    // if we found an item of the same type, add to its count
                    currentItem.count += item.count;
                    UpdateUI();
                    return;
                }
            }
        }

        // if the item is not stackable or we didn't find a matching item in the inventory, add it as a new item
        if (items.Count < inventorySize)
        {
            items.Add(item);
            UpdateUI();
        }
    }

    public void RemoveItem(int index)
    {
        if (index >= 0 && index < items.Count)
        {
            items.RemoveAt(index);
            UpdateUI();
        }
    }

    void UpdateUI()
    {
        // loop through the UI slots and update them to match the items in the inventory
        for (int i = 0; i < itemSlots.Count; i++)
        {
            InventorySlot slot = itemSlots[i].GetComponent<InventorySlot>();
            if (i < items.Count)
            {
                slot.SetItem(items[i]);
            }
            else
            {
                slot.Clear();
            }
        }
    }
}

public class InventoryItem
{
    public int id; // the ID of the item
    public string name; // the name of the item
    public bool stackable; // whether or not the item can be stacked
    public int count; // the number of items in the stack
    public int maxCount = 10; // the maximum number of items in a stack (default: 10)

    public InventoryItem(int id, string name, bool stackable, int count)
    {
        this.id = id;
        this.name = name;
        this.stackable = stackable;
        this.count = count;
    }
}

public class InventorySlot : MonoBehaviour
{
    private InventoryItem item; // the item in this slot

    public void SetItem(InventoryItem item)
    {
        this.item = item;

        // update the UI to show the item
        // for example, set the sprite and text to show the item name and count
    }

    public void Clear()
    {
        // clear the UI to show that there is no item in this slot
        // for example, clear the sprite and text
    }
}
```

