using UnityEngine;
using UnityEngine.UI;

public class QuestGiver : MonoBehaviour
{
    public string questText = "Collect 10 mushrooms";     // The text of the quest
    public int questAmount = 10;                          // The amount of items the player needs to collect
    public GameObject itemToCollect;                       // The item the player needs to collect (you can set it to Prefab)
    public int goldReward = 100;                           // The amount of gold the player gets for completing the quest

    private bool questActive = false;                      // Is the quest active?
    private int questProgress = 0;                         // The current progress of the quest
    private bool questCompleted = false;                   // Has the quest been completed?
    private bool isInRange = false;                         // Is the player in range to interact with the quest giver?
    private GameObject player;                              // A reference to the player object
    private Text questTextObject;                           // A reference to the quest text UI element
    private Button questButton;                             // A reference to the quest button UI element

    void Start()
    {
        // Get a reference to the player object
        player = GameObject.FindGameObjectWithTag("Player");

        // Find the quest text and quest button UI elements
        questTextObject = GameObject.FindGameObjectWithTag("QuestText").GetComponent<Text>();
        questButton = GameObject.FindGameObjectWithTag("QuestButton").GetComponent<Button>();

        // Disable the quest UI elements, since the quest is not active yet
        questTextObject.gameObject.SetActive(false);
        questButton.gameObject.SetActive(false);
    }

    void Update()
    {
        if (Input.GetKeyDown(KeyCode.E) && isInRange)
        {
            if (questActive)
            {
                CompleteQuest(); // If the player presses the E key and the quest is active, complete the quest
            }
            else if (!questCompleted)
            {
                StartQuest(); // If the player presses the E key and the quest is not active or completed, start the quest
            }
        }
    }

    void OnTriggerEnter(Collider other)
    {
        if (other.gameObject == player)
        {
            isInRange = true;
            if (!questActive && !questCompleted)
            {
                // Show the quest text UI element when the player enters the quest giver's range
                questTextObject.gameObject.SetActive(true);
                questButton.gameObject.SetActive(true);
                questTextObject.text = questText;
                questButton.GetComponentInChildren<Text>().text = "Talk";
            }
        }
    }

    void OnTriggerExit(Collider other)
    {
        if (other.gameObject == player)
        {
            isInRange = false;
            // Hide the quest text and quest button when the player leaves the quest giver's range
            questTextObject.gameObject.SetActive(false);
            questButton.gameObject.SetActive(false);
        }
    }

    void StartQuest()
    {
        // Set the quest as active
        questActive = true;

        // Instantiate the item the player needs to collect (you can also set the position and rotation of the item)
        Instantiate(itemToCollect);

        // Set the quest text and quest button text to show the goal of the quest
        questTextObject.text = "Collect " + questAmount + " " + itemToCollect.name + "s";
        questButton.GetComponentInChildren<Text>().text = "Complete";
    }

    void CompleteQuest()
    {
        // Check if the player has collected enough items
        if (questProgress >= questAmount)
        {
            // Give the player the gold reward
            player.GetComponent<Player>().AddGold(goldReward);

            // Disable the quest text and quest button, since the quest is completed
            questTextObject.gameObject.SetActive(false);
            questButton.gameObject.SetActive(false);

            // Set the quest as completed
            questCompleted = true;

            // Reset the quest progress and itemToCollect
            questProgress = 0;
            itemToCollect = null;

            // Show a message to the player that they have completed the quest
            Debug.Log("Quest completed! You received " + goldReward + " gold.");
        }
        else
        {
            // Show a message to the player that they have not completed the quest yet
            Debug.Log("You have not collected enough " + itemToCollect.name + "s yet.");
        }
    }

    public void CollectItem()
    {
        questProgress++;
    }
}
