import React, { useState } from 'react';
import { startOfWeek, addDays, format, addWeeks, subWeeks } from 'date-fns';

type Props = {
  currentWeekStart?: Date; // Optional prop to set initial week
};

const SwipeableWeekCalendar: React.FC<Props> = ({ currentWeekStart }) => {
  const [weekStart, setWeekStart] = useState<Date>(
    currentWeekStart || startOfWeek(new Date()),
  );

  const handleSwipeLeft = () => {
    setWeekStart(addWeeks(weekStart, 1));
  };

  const handleSwipeRight = () => {
    setWeekStart(subWeeks(weekStart, 1));
  };

  const days = Array.from({ length: 7 }, (_, i) => addDays(weekStart, i));

  return (
    <div className="swipeable-week-calendar">
      <button onClick={handleSwipeRight}>&lt;</button>

      <ul>
        {days.map(day => (
          <li key={day.toISOString()}>{format(day, 'eee do MMM')}</li>
        ))}
      </ul>

      <button onClick={handleSwipeLeft}>&gt;</button>
    </div>
  );
};

export default SwipeableWeekCalendar;
```

