```

然后我们创建一个 `WeekCalendar` 组件，这个组件负责显示整个周历，包括日期和提醒事项：

```typescript
// WeekCalendar.tsx
import React, { useState } from "react";
import { Reminder } from "./types";

interface WeekCalendarProps {
  reminders: Reminder[];
}

const WeekCalendar: React.FC<WeekCalendarProps> = ({ reminders }) => {
  const [baseDate, setBaseDate] = useState(new Date());

  const handlePrevWeek = () => {
    setBaseDate((prev) => {
      const newDate = new Date(prev);
      newDate.setDate(prev.getDate() - 7);
      return newDate;
    });
  };

  const handleNextWeek = () => {
    setBaseDate((prev) => {
      const newDate = new Date(prev);
      newDate.setDate(prev.getDate() + 7);
      return newDate;
    });
  };

  const weekdays = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];

  const generateWeekDays = () => {
    const weekDays: Date[] = [];
    const weekStartDate = new Date(baseDate);
    weekStartDate.setDate(baseDate.getDate() - baseDate.getDay());

    for (let i = 0; i < 7; i++) {
      const day = new Date(weekStartDate);
      day.setDate(weekStartDate.getDate() + i);
      weekDays.push(day);
    }

    return weekDays;
  };

  const handleClickDay = (date: string) => {
    const currentDateReminders = reminders.filter(
      (reminder) => reminder.date === date
    );
    console.log(currentDateReminders);
  };

  return (
    <div>
      <button onClick={handlePrevWeek}>&lt;</button>
      {generateWeekDays().map((day, index) => (
        <div
          key={day.toDateString()}
          onClick={() => handleClickDay(day.toDateString())}
        >
          <div>{weekdays[index]}</div>
          <div>{day.getDate()}</div>
        </div>
      ))}
      <button onClick={handleNextWeek}>&gt;</button>
    </div>
  );
};

export default WeekCalendar;
```

