```typescript
import React, { useState } from 'react';
import { format, startOfWeek, addDays, subWeeks, addWeeks } from 'date-fns';

interface IReminderItem {
  date: string;
  items: string[];
}

const initialReminder: IReminderItem[] = [
  { date: format(new Date(), 'yyyy-MM-dd'), items: ['Sample Reminder'] },
];

const WeeklyCalendar = () => {
  // 控制哪两个状态
  const [currentWeekStart, setCurrentWeekStart] = useState(startOfWeek(new Date()));
  const [reminders, setReminders] = useState(initialReminder);

  const handleDateClick = (date: Date) => {
    const formattedDate = format(date, 'yyyy-MM-dd');
    const reminder = reminders.find((r) => r.date === formattedDate);
    if (reminder) {
      alert(`Reminders on ${formattedDate}:\n${reminder.items.join('\n')}`);
    } else {
      alert(`No reminders found on ${formattedDate}`);
    }
  };

  const handlePrevWeek = () => {
    setCurrentWeekStart(subWeeks(currentWeekStart, 1));
  };

  const handleNextWeek = () => {
    setCurrentWeekStart(addWeeks(currentWeekStart, 1));
  };

  return (
    <div>
      <button onClick={handlePrevWeek}>&lt;</button>
      <button onClick={handleNextWeek}>&gt;</button>
      <div>
        {Array.from({ length: 7 }).map((_, index) => {
          const currentDate = addDays(currentWeekStart, index);
          return (
            <div key={index} onClick={() => handleDateClick(currentDate)}>
              {format(currentDate, 'eee dd MMM')}
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default WeeklyCalendar;
```

