import React, { useState } from "react";
import "./App.css";

type Reminder = {
  date: Date;
  message: string;
};

type CalendarProps = {
  reminders: Reminder[];
};

type DayProps = {
  date: Date;
  reminders: Reminder[];
  onClick: (date: Date) => void;
};

const Day: React.FC<DayProps> = ({ date, reminders, onClick }) => {
  const day = date.getDate();
  const reminder = reminders.find(
    (r) => r.date.toDateString() === date.toDateString()
  );

  return (
    <div className="day" onClick={() => onClick(date)}>
      {day}
      {reminder && <div className="reminder">{reminder.message}</div>}
    </div>
  );
};

const WeekCalendar: React.FC<CalendarProps> = ({ reminders }) => {
  const [currentDate, setCurrentDate] = useState(new Date());

  const startOfWeek = new Date(
    currentDate.setDate(currentDate.getDate() - currentDate.getDay() + 1)
  );

  const days = [...Array(7)].map((_, i) => {
    const day = new Date(startOfWeek);
    day.setDate(day.getDate() + i);
    return day;
  });

  const viewReminders = (date: Date) => {
    console.log(reminders);
  };

  return (
    <div className="week-calendar">
      <button
        onClick={() => setCurrentDate(new Date(currentDate.getTime() - 7 * 24 * 60 * 60 * 1000))}
      >
        &larr;
      </button>
      {days.map((date) => (
        <Day
          key={date.toISOString()}
          date={date}
          reminders={reminders}
          onClick={viewReminders}
        />
      ))}
      <button
        onClick={() => setCurrentDate(new Date(currentDate.getTime() + 7 * 24 * 60 * 60 * 1000))}
      >
        &rarr;
      </button>
    </div>
  );
};

const App = () => {
  const reminders: Reminder[] = [
    { date: new Date(), message: "Today's reminder" },
    { date: new Date(new Date().setDate(new Date().getDate() + 2)), message: "Upcoming event" },
  ];

  return (
    <div className="App">
      <WeekCalendar reminders={reminders} />
    </div>
  );
};

export default App;
