        zIndex: 1,
      });

      const listItemElements = Array.from(
        document.querySelectorAll('.list-item:not(:nth-child(' + (draggingIndex.value + 1) + '))')
      );

      for (const listItemElement of listItemElements) {
        const rect = listItemElement.getBoundingClientRect();

        if (rect.top < event.clientY && rect.bottom > event.clientY) {
          const listItemIndex = listItemElements.indexOf(listItemElement);
          const newList = [...list.value];
          newList.splice(draggingIndex.value, 1);
          newList.splice(listItemIndex, 0, list.value[draggingIndex.value]);
          list.value = newList;
          draggingIndex.value = listItemIndex;
          initialMouseY.value = mouseY;
          break;
        }
      }
    };

    const stopDrag = () => {
      if (draggingIndex.value === null) return;

      gsap.to(`.list-item:nth-child(${draggingIndex.value + 1})`, {
        y: 0,
        zIndex: 0,
        onComplete: () => {
          draggingIndex.value = null;
        },
      });

      document.removeEventListener('mousemove', doDrag);
      document.removeEventListener('touchmove', doDrag);
      document.removeEventListener('mouseup', stopDrag);
      document.removeEventListener('touchend', stopDrag);
    };

    return {
      list,
      startDrag,
    };
  },
};
