$font_size = 14;

$svg_template = <<<SVG
    <svg viewBox="0 0 200 200" xmlns="http://www.w3.org/2000/svg">
        <circle cx="$cx" cy="$cy" r="$radius" fill="red" />
        <text x="$cx" y="$cy" font-size="$font_size" fill="white"
            text-anchor="middle"
