function checkCollision(rect1, rect2) {
  return rect1.x < rect2.x + rect2.width &&
    rect1.x + rect1.width > rect2.x &&
    rect1.y < rect2.y + rect2.height &&
    rect1.y + rect1.height > rect2.y;
}

function update() {
  player.velocityY += 0.5;

  player.y += player.velocityY;

  if (player.y + player.height > canvas.height) {
    player.y = canvas.height - player.height; // добавим эту строку
    player.onGround = true;
    player.velocityY *= -0.5;
  } else if (player.y < 0) { // обработка верхней границы канваса
    player.y = 0;
    player.velocityY *= -0.5;
  } else {
    player.onGround = false;
  }

  platforms.forEach((platform, index) => {
    platform.x += platform.velocityX;

    if (platform.x + platform.width < 0) {
      platforms.splice(index, 1);
      return;
    }

    if (checkCollision(player, platform)) {
      const prevX = player.x - player.velocityX;
      const prevY = player.y - player.velocityY;

      const fromTop = prevY + player.height <= platform.y;
      const fromBottom = prevY >= platform.y + platform.height;
      const fromLeft = prevX + player.width <= platform.x;
      const fromRight = prevX >= platform.x + platform.width;

      if (fromTop) {
        player.y = platform.y - player.height;
        player.velocityY *= -0.5;
      } else if (fromBottom) {
        player.y = platform.y + platform.height;
        player.velocityY *= -0.5;
      } else if (fromLeft) {
        player.x = platform.x - player.width;
        player.velocityX = 0;
      } else if (fromRight) {
        player.x = platform.x + platform.width;
        player.velocityX = 0;
      }

      player.onGround = fromTop;
    }
  });

  // Плавное движение игрока по оси X
  player.x += player.velocityX;
  if (player.x < 0) player.x = 0;
  if (player.x + player.width > canvas.width) player.x = canvas.width - player.width;
}
```

