#include <Windows.h>
#include <iostream>
#include <gdiplus.h>

#pragma comment (lib, "Gdiplus.lib")

using namespace Gdiplus;

// Global variables
HDC hdcMem = NULL;
HBITMAP hBitmap = NULL;
HDC hdcSrc = NULL;
HDC hdcDst = NULL;
BITMAP bm = { 0 };

void UpdateLayeredWindow(HWND hwnd, HDC hdcDst)
{
    BLENDFUNCTION blend = { 0 };
    blend.BlendOp = AC_SRC_OVER;
    blend.SourceConstantAlpha = 255;
    blend.AlphaFormat = AC_SRC_ALPHA;

    POINT ptDst = { 0, 0 };
    SIZE sizeDst = { bm.bmWidth, bm.bmHeight };
    POINT ptSrc = { 0, 0 };
    COLORREF crKey = RGB(0, 0, 0);

    UpdateLayeredWindow(hwnd, hdcDst, &ptDst, &sizeDst, hdcSrc, &ptSrc, crKey, &blend, ULW_ALPHA);
}

LRESULT CALLBACK WndProc(HWND hwnd, UINT uMsg, WPARAM wParam, LPARAM lParam)
{
    switch (uMsg)
    {
    case WM_CREATE:
        hdcMem = CreateCompatibleDC(NULL);
        hBitmap = (HBITMAP)LoadImage(NULL, L"test.bmp", IMAGE_BITMAP, 0, 0, LR_LOADFROMFILE);
        if (hBitmap == NULL)
        {
            MessageBox(hwnd, L"Error loading bitmap", L"Error", MB_OK);
            PostQuitMessage(0);
        }
        SelectObject(hdcMem, hBitmap);
        GetObject(hBitmap, sizeof(bm), &bm);
        SetWindowPos(hwnd, HWND_TOPMOST, 0, 0, bm.bmWidth, bm.bmHeight, SWP_SHOWWINDOW);
        break;

    case WM_PAINT:
        hdcDst = BeginPaint(hwnd, NULL);
        BitBlt(hdcDst, 0, 0, bm.bmWidth, bm.bmHeight, hdcMem, 0, 0, SRCCOPY);
        EndPaint(hwnd, NULL);
        break;

    case WM_TIMER:
        // Scale bitmap
        hdcSrc = CreateCompatibleDC(NULL);
        HBITMAP hOldSrc = (HBITMAP)SelectObject(hdcSrc, hBitmap);

        int newWidth = bm.bmWidth * 2;
        int newHeight = bm.bmHeight * 2;

        Bitmap bmp(newWidth, newHeight);
        Graphics g(&bmp);
        g.DrawImage(&bmp, 0, 0, bm.bmWidth, bm.bmHeight);

        BitmapData bmpData;
        RECT rc = { 0, 0, bm.bmWidth, bm.bmHeight };
        bmp.LockBits(&rc, ImageLockModeRead, PixelFormat32bppARGB, &bmpData);

        hdcDst = GetDC(hwnd);

        BITMAPINFO bmi = { 0 };
        bmi.bmiHeader.biSize = sizeof(BITMAPINFOHEADER);
        bmi.bmiHeader.biWidth = bmpData.Width;
        bmi.bmiHeader.biHeight = bmpData.Height;
        bmi.bmiHeader.biPlanes = 1;
        bmi.bmiHeader.biBitCount = 32;
        bmi.bmiHeader.biCompression = BI_RGB;

        hBitmap = CreateDIBSection(hdcDst, &bmi, DIB_RGB_COLORS, NULL, NULL, 0);
        if (hBitmap == NULL)
        {
            MessageBox(hwnd, L"Error creating bitmap", L"Error", MB_OK);
            PostQuitMessage(0);
        }

        hdcMem = CreateCompatibleDC(hdcDst);
        HBITMAP hOldMem = (HBITMAP)SelectObject(hdcMem, hBitmap);

        UINT32* pSrc = (UINT32*)bmpData.Scan0;
        UINT32* pDst = (UINT32*)malloc(bmpData.Width * bmpData.Height * sizeof(UINT32));

        for (int i = 0; i < bmpData.Height; i++)
        {
            for (int j = 0; j < bmpData.Width; j++)
            {
                int indexSrc = i * bmpData.Stride / 4 + j;
                int indexDst = i * bmpData.Stride / 2 + j * 2;
                pDst[indexDst] = pSrc[indexSrc];
                pDst[indexDst + 1] = pSrc[indexSrc];
                pDst[indexDst + bmpData.Width] = pSrc[indexSrc];
                pDst[indexDst + bmpData.Width + 1] = pSrc[indexSrc];
            }
        }

        CopyMemory(bmpData.Scan0, pDst, bmpData.Width * bmpData.Height * sizeof(UINT32));

        SelectObject(hdcSrc, hOldSrc);
        SelectObject(hdcMem, hOldMem);
        free(pDst);

        UpdateLayeredWindow(hwnd, hdcDst);

        DeleteDC(hdcSrc);

        break;

    case WM_DESTROY:
        PostQuitMessage(0);
        break;

    default:
        return DefWindowProc(hwnd, uMsg, wParam, lParam);
    }
    return 0;
}

int WINAPI WinMain(HINSTANCE hInstance, HINSTANCE hPrevInstance, LPSTR lpCmdLine, int nShowCmd)
{
    // Initialize GDI+
    ULONG_PTR gdiplusToken;
    GdiplusStartupInput gdiplusStartupInput;
