    wcex.hbrBackground = (HBRUSH)(COLOR_WINDOW + 1);
    wcex.lpszClassName = L"MyClass";
    if (!RegisterClassEx(&wcex))
    {
        MessageBox(NULL, L"Error registering class", L"Error", MB_OK);
        return 0;
    }

    // Create window
    HWND hwnd = CreateWindowEx(WS_EX_LAYERED, L"MyClass", L"MyWindow", WS_POPUP, 0, 0, 0, 0, NULL, NULL, hInstance, NULL);
    if (hwnd == NULL)
    {
        MessageBox(NULL, L"Error creating window", L"Error", MB_OK);
        return 0;
    }

    // Set transparency
    SetLayeredWindowAttributes(hwnd, RGB(0, 0, 0), 255, LWA_COLORKEY | LWA_ALPHA);

    // Start timer
    SetTimer(hwnd, 1, 1000, NULL);

    // Message loop
    MSG msg;
    while (GetMessage(&msg, NULL, 0, 0))
    {
        TranslateMessage(&msg);
        DispatchMessage(&msg);
    }

