    GdiplusShutdown(gdiplusToken);
    DeleteDC(hdcMem);
    DeleteDC(hdcSrc);
    DeleteObject(hBitmap);
    ReleaseDC(hwnd, hdcDst);
    return (int)msg.wParam;
}

