LRESULT CALLBACK WndProc(HWND hwnd, UINT uMsg, WPARAM wParam, LPARAM lParam)
{
    switch (uMsg)
    {
    case WM_CREATE:
        hdcMem = CreateCompatibleDC(NULL);
        hBitmap = (HBITMAP)LoadImage(NULL, L"test.bmp", IMAGE_BITMAP, 0, 0, LR_LOADFROMFILE);
        if (hBitmap == NULL)
        {
            MessageBox(hwnd, L"Error loading bitmap", L"Error", MB_OK);
            PostQuitMessage(0);
        }
        SelectObject(hdcMem, hBitmap);
        GetObject(hBitmap, sizeof(bm), &bm);
        SetWindowPos(hwnd, HWND_TOPMOST, 0, 0, bm.bmWidth, bm.bmHeight, SWP_SHOWWINDOW);
        break;

    case WM_PAINT:
        hdcDst = BeginPaint(hwnd, NULL);
        StretchBlt(hdcDst, 0, 0, bm.bmWidth, bm.bmHeight, hdcMem, 0, 0, bm.bmWidth, bm.bmHeight, SRCCOPY);
        EndPaint(hwnd, NULL);
        break;

    case WM_TIMER:
        // Scale bitmap
        HBITMAP hOldBitmap = (HBITMAP)SelectObject(hdcMem, hBitmap);

        int newWidth = bm.bmWidth * 2;
        int newHeight = bm.bmHeight * 2;

        HDC hdcSrc = CreateCompatibleDC(NULL);
        HBITMAP hBitmapScale = CreateCompatibleBitmap(hdcSrc, newWidth, newHeight);
        HBITMAP hOldBitmapScale = (HBITMAP)SelectObject(hdcSrc, hBitmapScale);

        SetStretchBltMode(hdcSrc, HALFTONE);
        StretchBlt(hdcSrc, 0, 0, newWidth, newHeight, hdcMem, 0, 0, bm.bmWidth, bm.bmHeight, SRCCOPY);

        SelectObject(hdcSrc, hOldBitmapScale);
        DeleteDC(hdcSrc);

        SelectObject(hdcMem, hOldBitmap);
        DeleteObject(hBitmap);

        hBitmap = hBitmapScale;
        GetObject(hBitmap, sizeof(bm), &bm);

        hdcDst = GetDC(hwnd);
        UpdateLayeredWindow(hwnd, hdcDst);
        ReleaseDC(hwnd, hdcDst);

