    GetObject(hBitmap, sizeof(BITMAP), &bitmap);

    // Set the size of the window to the bitmap dimensions
    SetWindowPos(hWnd, NULL, 0, 0, bitmap.bmWidth, bitmap.bmHeight, SWP_NOZORDER | SWP_NOMOVE);

    // Center the window on the monitor
    CenterWindow(hWnd, bitmap.bmWidth, bitmap.bmHeight);

    // Set the window as transparent
    SetLayeredWindowAttributes(hWnd, RGB(0, 0, 0), 0, LWA_COLORKEY);

    // Display the bitmap in the window
    HDC hdc = GetDC(hWnd);
    HDC hdcMem = CreateCompatibleDC(hdc);
    HBITMAP hOldBitmap = (HBITMAP)SelectObject(hdcMem, hBitmap);
    SIZE size;
    BLENDFUNCTION blend;
    blend.BlendOp = AC_SRC_OVER;
    blend.BlendFlags = 0;
    blend.SourceConstantAlpha = 255;
    blend.AlphaFormat = AC_SRC_ALPHA;
    GetBitmapDimensionEx(hBitmap, &size);
    StretchBlt(hdc, (bitmap.bmWidth - size.cx) / 2, (bitmap.bmHeight - size.cy) / 2, size.cx, size.cy, hdcMem, 0, 0, bitmap.bmWidth, bitmap.bmHeight, SRCCOPY);
    UpdateLayeredWindow(hWnd, hdc, NULL, &size, hdcMem, NULL, RGB(0, 0, 0), &blend, ULW_ALPHA);
    SelectObject(hdcMem, hOldBitmap);
    DeleteDC(hdcMem);
    ReleaseDC(hWnd, hdc);

    // Show the window and enter the message loop
    ShowWindow(hWnd, SW_SHOW);
    MSG msg;
    while (GetMessage(&msg, NULL, 0, 0)) {
        TranslateMessage(&msg);
        DispatchMessage(&msg);
    }

    // Clean up
    DeleteObject(hBitmap);
    DestroyWindow(hWnd);

    return 0;
}
