def max_profit(B, a, m):
    money = m    # 起始资金
    cnt = 0    # 连续亏损次数
    max_cnt = 0    # 最大连续亏损次数
    max_profit = 0    # 当前最大利润
    for i in range(len(B)):
        if money <= ma:
            if B[i] < a:
                cnt += 1
                if cnt > max_cnt:
                    max_cnt = cnt
                if cnt == m:
                    return 0    # 破产
            else:
                money -= a
                cnt = 0
                money += B[i]
                max_profit = max(max_profit, money - m)
        elif money >= 2*ma and money < 3*ma:
            if B[i] < 2*a:
                cnt += 1
                if cnt > max_cnt:
                    max_cnt = cnt
                if cnt == m:
                    return 0
            else:
                money -= 2*a
                cnt = 0
                money += 2*B[i]
                max_profit = max(max_profit, money - m)
        elif money >= 3*ma and money < 4*ma:
            if B[i] < 3*a:
                cnt += 1
                if cnt > max_cnt:
                    max_cnt = cnt
                if cnt == m:
                    return 0
            else:
                money -= 3*a
                cnt = 0
                money += 3*B[i]
                max_profit = max(max_profit, money - m)
        elif money >= 4*ma:
            n = money // ma    # 可以买几个西瓜
            if B[i] < n*a:
                cnt += 1
                if cnt > max_cnt:
                    max_cnt = cnt
                if cnt == m:
                    return 0
            else:
                money -= n*a
                cnt = 0
                money += n*B[i]
                max_profit = max(max_profit, money - m)

