using Microsoft.AspNetCore.Builder;
using Microsoft.AspNetCore.Hosting;
using Microsoft.Extensions.Configuration;
using Microsoft.Extensions.DependencyInjection;
using Microsoft.Extensions.Hosting;
using Senparc.Weixin.Entities;
using Senparc.Weixin.MP;
using Senparc.Weixin.MP.Configuration;
using Senparc.Weixin.MP.MessageHandlers;
using Senparc.Weixin.MP.MvcExtension;
using Senparc.Weixin.MP.Regex;
using Senparc.Weixin.MP.TenPayLibV3;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;

namespace WeChatDemoV2
{
    public class Startup
    {
        public Startup(IConfiguration configuration)
        {
            Configuration = configuration;
        }

        public IConfiguration Configuration { get; }

        public void ConfigureServices(IServiceCollection services)
        {
            services.AddMvc();

            //配置微信公众号信息
            services.Configure<WeixinOptions>(Configuration.GetSection("Weixin"));

            services.AddSenparcWeixinServices(Configuration);//Senparc.Weixin 注册
            services.AddWeixinService<WxMessageService>();//注册微信服务
            services.AddSingleton<IMessageHander, WxMessageService>();//注入消息处理类
            services.AddSenparcGlobalServices(Configuration);//需要同时注册并启用 CO2NET 全局注册

        }

        public void Configure(IApplicationBuilder app, IWebHostEnvironment env)
        {
            app.UseDeveloperExceptionPage();

            //注意事项：在多租户系统中，必须使用路由前缀限制不同租户域名的访问
            var routeStr = Configuration.GetSection("Weixin")["RoutePrefix"];//从appsetting.json中读取路由地址
            if (!string.IsNullOrEmpty(routeStr))
            {
                app.UseRouter(routes =>
                {
                    routes.MapMiddlewareRoute(routeStr, appBuilder =>
                    {
                        appBuilder.UseMessageHandlerMiddleware(); //微信消息集成处理
                    });
                });
            }
            else
            {
                //全局注册使用 MessageHandlerMiddleware
                app.UseMessageHandlerMiddleware();

                app.UseEndpoints(endpoints =>
                {
                    endpoints.MapControllers();
                });
            }

        }
    }
}

